import { Component, Inject, EventEmitter, Output } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { DeckComponent } from './../deck/deck.component';

@Component({
  selector: 'app-card-collection',
  templateUrl: './card-collection.component.html'
})
export class CardCollectionComponent {
  public cards: Card[] = [];
  @Output() added = new EventEmitter<string>();
  deck: DeckComponent;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string, deck: DeckComponent) {
    http.get<Card[]>(baseUrl + 'card').subscribe(result => {
      this.cards = result;
    }, error => console.error(error));
    this.deck = deck;
  }

  public addCardToDeck(cardName: string) {
    this.deck.onAdded(cardName);
    //this.added.emit(cardName)
  }
}

interface Card {
  cardType: string;
  civilizations: string;
  manaCost: number;
  name: string;
  power: string;
  races: string;
  rulesText: string;
}
<h2>Card collection</h2>
<p *ngIf="!cards"><em>Loading...</em></p>
<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="cards">
  <thead>
    <tr>
      <th>Name</th>
      <th>Card type</th>
      <th>Civilizations</th>
      <th>Mana cost</th>
      <th>Rules text</th>
      <th>Power</th>
      <th>Races</th>
      <th></th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let card of cards">
      <td>{{ card.name }}</td>
      <td>{{ card.cardType }}</td>
      <td>{{ card.civilizations }}</td>
      <td>{{ card.manaCost }}</td>
      <td>{{ card.rulesText }}</td>
      <td>{{ card.power }}</td>
      <td>{{ card.races }}</td>
      <td><button class="btn btn-primary" (click)="addCardToDeck(card.name)">Add</button></td>
    </tr>
  </tbody>
</table>
